import './App.css';

const supplier = [
  { id: 1, name: 'Desi' },
  { id: 2, name: 'Dedi' },
  { id: 3, name: 'Dodi' },
];

const item = [
  { supplier_id: 1, item_name: 'Aqua', item_price: 3000 },
  { supplier_id: 1, item_name: 'Buku', item_price: 4000 },
  { supplier_id: 2, item_name: 'Buku', item_price: 2000 },
  { supplier_id: 3, item_name: 'Ember', item_price: 5000 },
];

// Function Group By Supplier
const groupBySupplier = supplier.map((s) => {
  const filItemSupplier = item.filter((i) => i.supplier_id === s.id);
  return { name: supplier, item_name: filItemSupplier };
});

console.log(groupBySupplier);
function App() {
  return (
    <div>
      <table>
        <thead>
          <tr>
            <th className="border">Name</th>
            <th className="border">Item Name</th>
            <th className="border">Price</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>aowdkaokdao</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default App;
