import './App.css';

const supplier = [
  { id: 1, name: 'Desi' },
  { id: 2, name: 'Dedi' },
  { id: 3, name: 'Dodi' },
];

const item = [
  { supplier_id: 1, name: 'Aqua', price: 3000 },
  { supplier_id: 1, name: 'Buku', price: 4000 },
  { supplier_id: 2, name: 'Buku', price: 2000 },
  { supplier_id: 3, name: 'Ember', price: 5000 },
];

// Function Group By Supplier
const groupBySupplier = supplier.map((s) => {
  const filItemSupplier = item.filter((i) => {
    return s.id === i.supplier_id;
  });
  return { supplier: s, supplier_item: filItemSupplier };
});

console.log(groupBySupplier);
function App() {
  return (
    <div>
      <table>
        <thead>
          <tr>
            <th className="border">Name</th>
            <th className="border">Item Name</th>
            <th className="border">Price</th>
          </tr>
        </thead>
        <tbody>
          {groupBySupplier.map((g) => {
            return (
              <>
                <tr>
                  <td>{g.name}</td>
                </tr>
              </>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default App;
